class Solution {
public:
    bool check(long long a, long long b)
    {
        if(a == 0) return false;

        a = (a > 0 ? 1 : -1);
        b = (b > 0 ? 1 : -1);

        return (a*b > 0);
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>req(nums.size(), 0);
        for(int i=0; i<nums.size(); i++)
        {
            req[i] = ((long long)target[i] - (long long)nums[i]);
        }

        stack<long long>st;
        long long prev = -1;
        long long ans = 0;

        for(long long i=0; i<nums.size(); i++)
        {
            if(i == 0 || st.empty())
            {
                st.push(req[i]);
                ans += (abs(req[i]));
                prev = req[i];
                continue;
            }

            bool sameSign = check(req[i], prev);
            //(req[i]*prev > 0);
            bool neg = (req[i] < 0);

            if(sameSign == true && neg == false)
            {
                long long top = st.top();
                if(req[i] >= top)
                {
                    ans += (req[i] - top);
                    st.push(req[i]);
                }
                else
                {
                    while(!st.empty() && st.top() >= req[i]) st.pop();

                    st.push(req[i]);
                }
            }
            else if(sameSign && neg)
            {
                long long top = st.top();
                if(abs(req[i]) >= abs(top))
                {
                    ans += (abs(req[i]) - abs(top));
                    st.push(req[i]);
                }
                else
                {
                    while(!st.empty() && abs(st.top()) >= abs(req[i])) st.pop();

                    st.push(req[i]);
                }
            }
            else
            {
                while(!st.empty()) st.pop();

                ans += (abs(req[i]));
                st.push(req[i]);
            }
            prev = req[i];
        }

        return ans;
    }
};
