class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        deltas = []
        for x, y in zip(nums, target):
            deltas.append(x - y)
        pda = [deltas[0]]
        for i in range(1, len(deltas)):
            pda.append(deltas[i] - deltas[i - 1])
        resa = 0
        resb = 0
        for v in pda:
            if v < 0:
                resa -= v
            else:
                resb += v
        return max(resa, resb)
