class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int>v;
        for(int i=0;i<nums.size();i++){
            int diff=target[i]-nums[i];
            v.push_back(diff);
        }

        long long ans=0;
        int mx=0;
        int is_pos=0;

        //for(int i=0;i<nums.size();i++) cout<<v[i]<<" ";

        for(int i=0;i<nums.size();i++){
            if(v[i]==0){
                if(is_pos==1){
                    //ans+=mx;
                    mx=0;
                }
                else if(is_pos==-1){
                    //ans+=(-mx);
                    mx=0;
                }
                is_pos=0;
            }
            if(v[i]>0){
                if(is_pos==0){
                    ans+=v[i];
                    is_pos=1;
                    mx=v[i];
                }
                else if(is_pos==-1){
                    ans+=v[i];
                    is_pos=1;
                    mx=v[i];
                }
                else{
                    ans += max(v[i] - v[i - 1], 0); 
                    mx=max(mx,v[i]);
                }
            }
            else{
                if(is_pos==0){
                    ans+=abs(v[i]);
                    is_pos=-1;
                    mx=v[i];
                }
                else if(is_pos==-1){
                    ans+=max(abs(v[i])-abs(v[i-1]),0);
                    mx=min(mx,v[i]);
                }
                else{
                    ans+=abs(v[i]);
                    is_pos=-1;
                    mx=v[i];
                }
            }
            //cout<<ans<<" ";
        }

        // if(mx>0) ans+=mx;
        // else ans+=(-mx);

        return ans;
    }
};
