class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums);
        h = [target[i]-nums[i] for i in range(n)];
        print(h);
        maxi = 0;
        hup = [0]*n;
        sz = 0;
        res = 0;
        pre = 0;
        lft = 0 ;
        for i in range(n):
            if h[i]==0:
                res = res + maxi;
                maxi = 0;
                continue;
            if i==0 or (i>0 and h[i-1]==0):
                maxi = abs(h[i]);
            else:
                if h[i]*h[i-1]>0:
                    if abs(h[i])>= abs(h[i-1]):
                        maxi = abs(h[i]);
                    else:
                        res = res + maxi-abs(h[i]);
                        maxi = abs(h[i]);
                else:
                    res = res + maxi;
                    sz = 0;
                    maxi = abs(h[i]);
        res = res + maxi;
        return res;
