class Solution {
public:
    long long int merge(vector<int> &v , int l , int h)
    {
        if(h<l) return 0;
        if(l==h) return v[l];
        int mn=INT_MAX;
        int j=-1;
        for(int i=l;i<=h;i++)
        {
            if(mn>=v[i])
            {
                mn=v[i];
                j=i;
            }
        }
        for(int i=l;i<=h;i++)
        {
            v[i]-=mn;
        }
        int k=j;
        while(k>=0 && v[k]==0) k--;
        int x=merge(v,l,k);
        k=j;
        while(k<=h && v[k]==0) k++;
        int y=merge(v,k,h);

        return mn+x+y;

    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> vec;
        int n= nums.size();
        for(int i=0;i<n;i++)
        {
            vec.push_back(target[i]-nums[i]);
        }


        int i=0;
        int j=0;
        long long int ans=0;
        while(i<n)
        {
            vector<int> v;
            if(vec[i]>0)
            {
                while(i<n && vec[i]>0)
                {
                    v.push_back(vec[i++]);
                }
            }
            else if(vec[i]<0)
            {
                while(i<n && vec[i]<0)
                {
                    v.push_back(abs(vec[i++]));
                }
            }
            else
            {
                i++;
            }
            ans+=merge(v,0,v.size()-1);
        }

        return ans;
    }
};
