class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
    ios::sync_with_stdio(false); 
    cin.tie(nullptr);
        if (nums.size() == 0) {
            cout << "Empty input vectors provided!" << endl;
            return 0;
        }

        vector<int> diff(nums.size());
        for (int i = 0; i < nums.size(); ++i) {
            diff[i] = target[i] - nums[i];
        }

        long long total_operations = 0;
        long long current_segment_diff = 0;

        for (int i = 0; i < nums.size();i++) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                long long additional_operations = abs(diff[i]) - abs(current_segment_diff);
                if (additional_operations > 0) {
                    total_operations += additional_operations;
                }
                current_segment_diff = diff[i];
            } else {
                total_operations += abs(diff[i]);
                current_segment_diff = diff[i];
            }
        }

        return total_operations;
    }
};
