class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         long long  n=nums.size();
        vector< long long >v(n,0);
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
            // cout<<v[i]<<" ";
        }

             long long  ans=0,p=0,p1=0;
        for(int i=0;i<n;i++){
            if(v[i]==0){
                p=0;
                p1=0;
                continue;
            }else if(v[i]>0){
                p1=0;
                 long long  q=v[i]-p;
                if(q>0) ans+=q;
                p=v[i];
            }else if(v[i]<0){
                p=0;
                 long long  q=p1-v[i];
                if(q>0) ans+=abs(q);
                p1=v[i];
            }

        }
    return ans;
    }
};
