class Solution {
public:
    long long minimumOperations(vector<int>& arr, vector<int>& v) {
        int n = size(arr);
        long long ans = 0, temp = 0;
        bool flag = false;
        vector<int> diff(n, 0);
        for(int i = 0; i < n; i++)
            diff[i] = v[i] - arr[i];
        ans = temp = abs(diff[0]);
        flag = (diff[0] >= 0);
        for(int i = 1; i < n; i++){
            if(diff[i] < 0 and flag){
                flag = !flag;
                temp = abs(diff[i]);
                ans += temp;
            }
            else if(diff[i] >= 0 and !flag){
                temp = abs(diff[i]);
                ans += temp;
                flag = !flag;
            }
            else if(temp >= abs(diff[i])) temp = abs(diff[i]);
            else{
                ans += (abs(diff[i]) - temp);
                temp = abs(diff[i]);
            }
        }
        return ans;
    }
};
