class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0;
        vector<long long>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=target[i]-nums[i];
        }
        vector<int>tmp;
        int sig=0;
        for(int i=0;i<n;i++){
            if(sig==0){
                if(diff[i]>0){
                    sig=1;tmp.push_back(diff[i]);
                }else if(diff[i]<0){
                    sig=-1;tmp.push_back(-diff[i]);
                }
            }
            else{
                if(diff[i]>0 && sig==1){
                    tmp.push_back(diff[i]);continue;
                }else if(diff[i]<0 && sig==1){
                    ans+=tmp[0];
                    int extra=tmp[0];

                    for (int i=1;i<tmp.size();i++) {
                        if (tmp[i] <= extra) { 
                            extra = tmp[i];
                        } else {
                            ans+=tmp[i]-extra; 
                            extra=tmp[i];
                        }
                    }
                    tmp.clear();
                    tmp.push_back(-diff[i]);sig=-1;continue;
                }else if(diff[i]==0 && sig==1){
                    ans+=tmp[0];
                    int extra=tmp[0];

                    for (int i=1;i<tmp.size();i++) {
                        if (tmp[i] <= extra) { 
                            extra = tmp[i];
                        } else {
                            ans+=tmp[i]-extra; 
                            extra=tmp[i];
                        }
                    }
                    tmp.clear();sig=0;continue;
                }

                if(diff[i]<0 && sig==-1){
                    tmp.push_back(-diff[i]);continue;
                }else if(diff[i]>0 && sig==-1){
                    ans+=tmp[0];
                    int extra=tmp[0];

                    for (int i=1;i<tmp.size();i++) {
                        if (tmp[i] <= extra) { 
                            extra = tmp[i];
                        } else {
                            ans+=tmp[i]-extra; 
                            extra=tmp[i];
                        }
                    }
                    tmp.clear();
                    tmp.push_back(diff[i]);sig=1;continue;
                }else if(diff[i]==0 && sig==-1){
                    ans+=tmp[0];
                    int extra=tmp[0];

                    for (int i=1;i<tmp.size();i++) {
                        if (tmp[i] <= extra) { 
                            extra = tmp[i];
                        } else {
                            ans+=tmp[i]-extra; 
                            extra=tmp[i];
                        }
                    }
                    tmp.clear();sig=0;continue;
                }
            }
        }
        if(tmp.size()>0){
            ans+=tmp[0];int extra=tmp[0];
            for (int i=1;i<tmp.size();i++) {
                if (tmp[i] <= extra) { 
                    extra = tmp[i];
                } else {
                    ans+=tmp[i]-extra; 
                    extra=tmp[i];
                }
            }
        }
        return ans;
    }
};
