    vector<long long > sub(nums.size(),0);
    long long res=0;
    int mn=(1<<30);
    for(int i=0;i<sub.size();i++){
        sub[i]=target[i]-nums[i];
//        mn=min(mn,sub[i]);
    }
    res= abs(sub[0]);
        for(int i=1;i<sub.size();i++){
