class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ret = []
        for i in range(len(nums)):
            if i == 0:
                ret.append(abs(nums[i]-target[i]))
            else:
                if (nums[i]-target[i])*(nums[i-1]-target[i-1]) < 0:
                    ret.append(ret[-1]+abs(nums[i]-target[i]))
                elif abs(nums[i]-target[i])<=abs(nums[i-1]-target[i-1]):
                    ret.append(ret[-1])
                else:
                    ret.append(ret[-1]+abs(nums[i]-target[i])-abs(nums[i-1]-target[i-1]))
        return ret[-1]
