class Solution {
public:
    long long minimumOperations(vector<int> nums, vector<int> target) {
        int n = nums.size(), csf = 0;
        vector<int> diff(n);
        long long ops = 0;

        for (int i = 0; i < n; ++i) {
            diff[i] = target[i] - nums[i];
        }

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int addops = abs(diff[i]) - abs(csf);
                if (addops > 0) {
                    ops += addops;
                }
                csf = diff[i];
            } else {
                ops += abs(diff[i]);
                csf = diff[i];
            }
        }
        return ops;
    }
};
