class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        long long ans=0ll;
        vector<int> v(n);
        for(int i=0;i<n;i++){
            v[i]=target[i]-nums[i];
        }
        ans+=abs(v[0]);
        int prev=v[0];
        for(int i=1;i<n;i++){
            if(prev==0){
                ans+=abs(v[i]);
                prev=v[i];
            }
            else if(v[i]==0){
                prev=v[i];
            }
            else if((prev<0 && v[i]>0) || (prev>0 && v[i]<0)){
                ans+=abs(v[i]);
                prev=v[i];
            }
            else{
                int pr=abs(prev);
                int vi=abs(v[i]);
                if(pr>=vi){
                    prev=v[i];
                }
                else{
                    ans+=(vi-pr);
                    prev=v[i];
                }
            }
        }
        return ans;
    }
};
