class Solution {
public:
    long long minimumOperations(vector<int>& n, vector<int>& t){
        int s = n.size();
        vector<long long> d(s);
        for(int i=0; i<s; i++){
            d[i] = t[i] - n[i];
        }

        long long cnt = 0;
        long long maxi = 0;

        for(int i=0; i<s; i++){
            if(i == 0 || (d[i-1] * d[i] > 0)){
                long long dif = abs(d[i]) - abs(maxi);
                if(dif > 0){
                    cnt += dif;
                }
                maxi = d[i];
            } 
            else{
                cnt += abs(d[i]);
                maxi = d[i];
            }
        }
        return cnt;
    }
};
