class Solution {
public:
     bool isPrime(int n)
{

    if (n <= 1)
        return false;


    for (int i = 2; i <= n / 2; i++)
        if (n % i == 0)
            return false;

    return true;
}
    int calculateSum(int n)
{
    if (n <= 0)
       return 0;

    int fibo[n+1];
    fibo[0] = 0, fibo[1] = 1;

    // Initialize result
    int sum = fibo[0] + fibo[1];

    // Add remaining terms
    for (int i=2; i<=n; i++)
    {
        fibo[i] = fibo[i-1]+fibo[i-2];
        sum += fibo[i];
    }

    return sum;

}
