    long long solve(vector<int>& arr, vector<int>& k)
    {
        int n=arr.size();
        vector<int>ans(n,0);
        for(int i=0;i<n;i++)
        {
            ans[i]=k[i]-arr[i];
        }
        int pos=0;
        long long cnt=0,res=0;
        res=abs(ans[0]);
        cnt=res;
        if(ans[0]>=0)
        {
            pos=1;
        }
        for(int i=1;i<n;i++)
        {
            if(ans[i]<0 && pos==1)
            {
                res+=abs(ans[i]);
                cnt=abs(ans[i]);
                pos=0;
                continue;

            }
            if(ans[i]>=0 && pos==0)
            {
                res+=abs(ans[i]);
                cnt=abs(ans[i]);
                pos=1;
                continue;

            }

            if(cnt>=abs(ans[i]))
            {
                cnt=abs(ans[i]);
                continue;
            }
            else{
                res+=abs(ans[i])-cnt;
                cnt=abs(ans[i]);
            }
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        return solve(nums,target);
    }
