#include <vector>
#include <cmath>
using namespace std;

class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        int n = nums.size();
        vector<int> diff(n, 0);
        for (int i = 0; i < n; i++) {
            diff[i] = tar[i] - nums[i];
        }

        int val = 0;
        if (diff[0] >= 0) val = 1;
        long long temp = abs(diff[0]), ans = abs(diff[0]);
        for (int i = 1; i < n; i++) {
            if (diff[i] < 0 && val == 1) {
                temp = abs(diff[i]);
                ans += temp;
                val^=1;
            }
            if (diff[i] >= 0 && val == 0) {
                temp = abs(diff[i]);
                ans += temp;
                val^=1;
            }
            if (temp < abs(diff[i])) {
                ans += abs(diff[i]) - temp;
            } 
            temp = abs(diff[i]);
        }

        return ans;
    }
};
