class Solution {
public:
bool check(int inc[],int dec[],int n){
    for(int i=0;i<n;i++){
        if(inc[i] || dec[i])return 0;
    }return 1;
}
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        int inc[n];long long ans=0;
        int dec[n];
        for(int i=0;i<n;i++){
            inc[i]=0;dec[i]=0;
        }
        for(int i=0;i<n;i++){
            if(nums[i]<target[i])inc[i]=target[i]-nums[i];
            else{dec[i]=-target[i]+nums[i];}
        }
        while(!check(inc,dec,n)){
            for(int i=0;i<n;i++){int j,k;
                if(inc[i]){j=i;
                int mi=INT_MAX;
                for(int l=j;l<=n-1 && inc[l];l++){k=l;mi=min(mi,inc[l]);}
                for(int l=j;l<=k;l++)inc[l]-=mi;
                ans+=mi;i=k+1;
                }
            }
            for(int i=0;i<n;i++){int j,k;
                if(dec[i]){j=i;k=i;
                int mi=INT_MAX;
                for(int l=j; l<=n-1 && dec[l];l++){k=l;mi=min(mi,dec[l]);}
                for(int l=j;l<=k;l++)dec[l]-=mi;
                ans+=mi;i=k+1;
                }
            }   
    }return ans;}
};
