class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long int> ans;
        for(int i=0;i<nums.size();i++){
            ans.push_back(target[i]-nums[i]);
        }
        long long int a=0;
        long long int b=0;
        for(int i=0;i<ans.size();i++){
            if(i==0 || (ans[i-1]>0 && ans[i]>0)||(ans[i-1]<0 && ans[i]<0)){
                if(abs(0-ans[i])-abs(0-b)>0){
                    a+=abs(0-ans[i])-abs(0-b);
                }
                    b=ans[i];

            }
            else{
                a+=abs(0-ans[i]);
                b=ans[i];
            }
        }
        return a;
    }
};
