class Solution:
  def minimumOperations(self, nums: List[int], target: List[int]) -> int:
    n = len(nums)
    diffs = [target[i] - nums[i] for i in range(n)]

    ret = abs(diffs[0])
    for i in range(1, n):
      if diffs[i - 1] * diffs[i] <= 0:
        ret += abs(diffs[i])
      else:
        if diffs[i] < 0:
          if diffs[i] < diffs[i - 1]:
            ret += abs(diffs[i] - diffs[i - 1])
        else:
          if diffs[i] > diffs[i - 1]:
            ret += abs(diffs[i] - diffs[i - 1])
    return ret
