class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        for (int i = 0; i < target.size(); i++) {
            target[i] -= nums[i];
        }
        vector<int> diff(target.size());
        for (int i = 0; i + 1 < target.size(); i++) {
            diff[i] = target[i + 1] - target[i];
        }
        diff.push_back(target[0]);
        diff.push_back(-target[target.size() - 1]);
        long long ans = 0;
        for (int i = 0; i < diff.size(); i++) {
            ans += abs(diff[i]);
        }
        ans /= 2;
        return ans;
    }
};
