class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ans=0
        preva=0
        n=len(nums)
        d=[0]*n
        dp=[0]*n
        for i in range(len(nums)):
            d[i]=target[i]-nums[i]
        # dp[0]=abs(d[0])
        # preva=dp[0] if d[0]>0 else 0
        # prevs=dp[0] if d[0]<0 else 0
        prev=d[0]
        ans+=abs(prev)
        for i in range(1,n):
            # prevs=preva-d[i-1]
            if d[i]>0:
                if d[i-1]>0:
                    ans+=max(d[i]-d[i-1],0)
                else:
                    ans+=d[i]
                # preva=step
                # prevs=preva-d[i-1]
            if d[i]<0:
                if d[i-1]<0:
                    ans+=max(-d[i]+d[i-1],0)
                else:
                    ans+=-d[i]
        return ans
