class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long int> diff;
        int len = nums.size();
        for(int i=0; i<len; ++i)
            diff.push_back(target[i] - nums[i]);

        long long int ans = abs(diff[0]), cnt = -diff[0];
        for(int i=1; i<len; ++i){
            if( diff[i] > 0 ){
                if( diff[i] * cnt > 0 ){
                    ans += abs(diff[i]);
                    cnt = -diff[i];
                }else if( diff[i] + cnt > 0 )
                    ans += (diff[i] + cnt);
                cnt = -diff[i];
            }else if ( diff[i] < 0 ){
                if( diff[i] * cnt > 0 ){
                    ans += abs(diff[i]);
                    cnt = -diff[i];
                }else if( diff[i] + cnt < 0 )
                    ans += abs(diff[i] + cnt);
                cnt = -diff[i];
            }else{
                cnt = 0;
            }
        }
        return ans;
    }
};
