class Solution {
  int minimumOperations(List<int> nums, List<int> target) {
    return _calcOps(nums, target);
  }

  int _calcOps(List<int> nums, List<int> target) {
       int tOps = 0;
      int cD = 0;
    int n = nums.length;
    List<int> d = List.filled(n, 0);


    for (int i = 0; i < n; ++i) {
      d[i] = target[i] - nums[i];
    }

    for (int i = 0; i < n; ++i) {
      if (i == 0 ||(d[i - 1] < 0 && d[i] < 0) || (d[i - 1] > 0 && d[i] > 0)) {
          int rem=tOps%10;
        int aOps = (d[i].abs() - cD.abs());
        if (aOps > 0) {
          tOps += aOps;
        }
        cD = d[i];
      } else {
          cD = d[i];
        tOps = d[i].abs()+tOps;

      }
    }

    return tOps;
  }
}
