class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        data=[t-n for t,n in zip(target,nums)]
        prev=data[0]
        res=abs(data[0])
        for i in data[1:]:
            diffs=abs(i)-abs(prev)
            if (i>=0 and prev>=0) or (i<=0 and prev<=0):
                if diffs>0:
                    res+=diffs
            else:
                res+=abs(i)

            prev=i
        return res
