class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res=0,curr=0;
        int len=nums.size();
        vector<int> temp(len);

        for(int i=0;i<len;i++){
            temp[i]=target[i]-nums[i];
        }

        for(int i=0;i<len;i++){
            if(i==0 || (temp[i-1]<0 && temp[i]<0) || (temp[i-1]>0 &&temp[i]>0)){
                long long dif=abs(temp[i])-abs(curr);
                if(dif>0){
                    res+=dif;
                }
            }
            else{
                res+=abs(temp[i]);
            }
            curr=temp[i];
        }
        return res; 
    }
};
