class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [target[i] - nums[i] for i in range(len(nums))]
        res = sum(abs(d) for d in diffs)
        for i, d in enumerate(diffs[:-1]):
            if d * diffs[i+1] > 0:
                res -= min(abs(d), abs(diffs[i+1]))
        return res
