class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long[] arr = new long[nums.length];
        int n = nums.length;
        for(int i=0;i<n;i++){
            arr[i] = target[i]-nums[i];
        }
        long minOp = Long.MIN_VALUE;
        long minNeg = 0, maxPos = 0;

        // Traverse the array
        long cost = 0;
        long stackheight = 0;

        for(int i=0;i<n;i++){
            long x = arr[i];
            if(x<0){
                if(stackheight>0){
                    cost+=Math.abs(x);
                }
                else{
                    cost += Math.max(stackheight-x, 0);}
            }
            else{
                if(stackheight<0){
                    cost+=x;
                }
                else{cost += Math.max(x - stackheight, 0);}
            }

        stackheight = x;}

        // Return minOp
        return cost;
    }
}
