class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0;i<n;i++) {
            target[i] -= nums[i];
            //cout<<i<<" "<<target[i]<<endl;
        }
        long long ans = abs(target[0]);
        //cout<<ans<<endl;
        for(int i=1;i<n;i++) {
            ans += abs(target[i]);
            if((target[i] > 0 && target[i-1] > 0) || (target[i] < 0 && target[i-1] < 0)) {
                int tmp = min(abs(target[i]), abs(target[i-1]));
                //cout<<i<<" tmp "<<tmp<<endl;
                ans -= tmp;  
            } 
            //cout<<i<<" "<<ans<<endl;
        }
        return ans;
    }
};
