class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int i,n=a.size();
        vector<int>c(n);
        for(i=0;i<n;i++) c[i]=b[i]-a[i];
        // for(int i=0;i<n;i++) c[i]=abs(b[i])

        long long res=abs(c[0]),temp=c[0];
        for (i=1;i<n; i++) {
            int t=abs(temp);
            if(c[i]>0 && c[i-1]>0){
                if (c[i]>t) {
                    res+=c[i]-t;
                }
            }
            else if(c[i]<0 && c[i-1]<0){
                if (abs(c[i])>t) {
                    res+=abs(c[i])-t;
                }
            } 
            else res += abs(c[i]);
            temp=c[i];
        }

        return res;
    }
};
