class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diffs(n);
        for (int i = 0; i < n; i++) {
            diffs[i] = target[i] - nums[i];
        }
        long long operations = 0;
        long long currentDifference = 0;
        for (int i = 0; i < n; i++) {
            if (i == 0 || diffs[i] != diffs[i - 1]) {
                operations += abs(diffs[i] - currentDifference);
                currentDifference = diffs[i];
            }
        }
        operations += abs(currentDifference);

        return operations/2;
    }
};
