class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long opr=0;
        // int curr=0;
        int prev=0;
        for(int i=0;i<nums.length;i++){
            int curr=target[i]-nums[i];
            if(prev>0&& curr>0){
                if(prev>=curr){
                    prev=curr;
                    continue;
                }else{
                    opr+=curr-prev;
                    prev=curr;
                    continue;
                }
            }
            if(prev<0&& curr<0){
                if(curr>=prev){
                    prev=curr;
                    continue;
                }else{
                    opr+=-(curr-prev);
                    prev=curr;
                    continue;
                }                
            }
                prev=curr;
            if(curr<0) curr=-curr;
            opr+=curr;
        }
        return opr;
    }
}
