class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
         int n = nums.size();
         long long ans = 0;
        vector<long long> diff(n);
        for (int i = 0; i < n; ++i) {
            diff[i] = (long long)target[i] - (long long)nums[i];
        }
        long long curr= 0;
        for (int i = 0; i < n; ++i) {
            if (i == 0 || (diff[i] > 0 && diff[i - 1] > 0) || (diff[i] < 0 && diff[i - 1] < 0)) {
                long long moreops = abs(diff[i]) - abs(curr);
                if (moreops > 0) {
                    ans += moreops;
                }
                curr = diff[i];
            } else {
                ans += abs(diff[i]);
                curr = diff[i];
            }
        }
        return ans;
    }
};
