        long long int ans = 0;
        int having = 0;
        for(int i=0;i<n;i++){
            if(v[i] == t[i]) having = 0;
            else if(v[i] < t[i]){
                if(having <= 0){
                    ans += t[i] - v[i];
                    having = t[i] - v[i];
                }
                else{
                    ans += max(t[i] - v[i] - having, 0);
                    having = t[i] - v[i];
                }
            }
            else{
                if(having >=0 ){
                    ans += abs(t[i] - v[i]);
                    having = t[i] - v[i];

                }
                else{
                    ans += max(abs(t[i] - v[i]) + having, 0);
                    having = t[i] - v[i];
                }
            }
            // cout<<ans<<" ";
        }


        return ans;
    }
