const int N = 2e5 + 10; // check the limits


class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll ans = 0;
        ll last = 0;
        for (int i = 0; i < nums.size(); i++) {
            int x = target[i] - nums[i];
            if (last >= 0) {
                if (x >= 0) {
                    if (last >= x) {
                        last = x;
                    } else {
                        ans += (x - last);
                        last = x;
                    }
                } else {
                    ans += abs(x);
                    last = x;
                }
            } else {
                if (x >= 0) {
                    ans += x;
                    last = x;
                } else {
                    if (x >= last) {
                        last = x;
                    } else {
                        ans += (last - x);
                        last = x;
                    }
                }
            }
        }
        return ans;
    }
};
