class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        dif = []
        for i in range(len(nums)):
            dif.append(target[i] - nums[i])
        print(dif)
        res = 0
        positive = True
        isLastZero = False
        last = 0
        if dif[0] < 0:
            positive = False
        for d in dif:
            if d > 0:
                if isLastZero:
                    positive = True
                    res += d
                    last = d
                    isLastZero = False
                elif positive:
                    res += d - min(last, d)
                    last = d
                else:
                    positive = True
                    res += d
                    last = d
            elif d < 0:
                if isLastZero:
                    res += -d
                    last = -d
                    positive = False
                    isLastZero = False
                if positive:
                    res += -d
                    last = -d
                    positive = False
                else:
                    res += -d
                    res -= min(last, -d)
                    last = -d
            else:
                isLastZero = True
        return res
