    long long minimumOperations(vector<int>& nums, vector<int>& tgt) {
        vector<int> diff(nums.size());

        //  for (size_t i = 0; i < nums.size(); ++i) {
        //     diff[i] = target[i] - nums[i];
        // }


        for (size_t i = 0; i < nums.size(); ++i) {
            diff[i] = tgt[i] - nums[i];
        }

        long long totalOps = 0;

        int curSegDiff = 0;


//         for (int i = 0; i < n; i++) {
