//             }

//             currentDifference = requiredDifference;
//         }

        for (size_t i = 0; i < diff.size(); ++i) {


            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {

                int addOps = abs(diff[i]) - abs(curSegDiff);


                if (addOps > 0) {
                    totalOps += addOps;
                }
                curSegDiff = diff[i];
            } 

else {
                totalOps += abs(diff[i]);

    curSegDiff = diff[i];
            }
        }



        return totalOps;
