class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) 
    {
        long long ans=0,n=nums.size();
        vector<int>vec(nums.size());
        for(int i=n-1;i>=0;i--)
        {
            vec[i]=nums[i]-target[i];
        }
        for(int i=0,inc=0,dec=0;i<n;i++)
        {
            long long temp=vec[i];
            ans+=abs(temp);
            for(int j=i;j<n;j++)
            {
                if(temp*vec[j]<=0)
                {
                    break;
                }
                if(temp<0&&vec[j]>temp)
                {
                    temp=vec[j];
                }
                if(temp>0&&vec[j]<temp)
                {
                    temp=vec[j];
                }
                vec[j]-=temp;
            }
        }
        return ans;
    }
};
