class Solution {
private:
    long long solve(int ind, int n, vector<int>& diff, vector<long long>& dp){
        if(ind >= n){
            return 0;
        }

        if(dp[ind] != -1){
            return dp[ind];
        }

        if(diff[ind] > 0){
            // check what you did in the last index becuase we are dealing with subarrays
            int prevInd = ind - 1;
            if(prevInd >= 0){
                if(diff[prevInd] > 0){
                    if(diff[prevInd] > diff[ind]){
                        return dp[ind] = solve(ind + 1, n, diff, dp);
                    }else{
                        return dp[ind] = (diff[ind] - diff[prevInd]) + solve(ind + 1, n, diff, dp);
                    }
                }else{
                    return dp[ind] = (diff[ind]) + solve(ind + 1, n, diff, dp);     
                }
            }else{
                return dp[ind] =  diff[ind] + solve(ind + 1, n, diff, dp);

            }
        }else if(diff[ind] < 0){
            int prevInd = ind - 1;

            if(prevInd >= 0){
                if(diff[prevInd] <= 0){
                     if(abs(diff[prevInd]) >= abs(diff[ind])){
                    return dp[ind] = solve(ind + 1, n, diff, dp);
                }else{
                    return dp[ind] = abs(diff[ind]) - abs(diff[prevInd]) + solve(ind + 1, n, diff, dp);
                }
                }else{
                    return dp[ind] = abs(diff[ind]) + solve(ind + 1, n, diff, dp);
                }

            }else{
                return dp[ind] = abs(diff[ind]) + solve(ind + 1, n, diff, dp);
            }
        }

            return dp[ind] = solve(ind + 1, n, diff, dp);



    }
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n);

        for(int ind = 0; ind < n; ind++){
            diff[ind] = target[ind] - nums[ind];
        }

        vector<long long> dp(n, -1);
        return solve(0, n, diff, dp);
    }
};
