class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<long long>v;
        long long int c=0,f=0;
        for(int i=0;i<nums.size();i++){
            v.push_back(target[i]-nums[i]);
        }
        c+=abs(v[0]);
        if(v[0]<0) f=-1;
        for(int i=1;i<nums.size();i++){
            if(f==0 and v[i]>=0) c-=v[i-1]-max(v[i],v[i-1]);
            else if(f==-1 and v[i]<0) c-=abs(v[i-1])-abs(min(v[i],v[i-1]));
            else c+=abs(v[i]);
            if(v[i]<0) f=-1;
            else f=0;
        }
        return c;
    }
};
