function minimumOperations(nums: number[], target: number[]): number {
  // 1 2 4 3 4 5 2 3 4
  //     4,  1,2, 1,2
  // 1 2 10 8 100 1 4
  // 10 100 4
  //
  // 10 5 4
  const n = nums.length
  let ans = 0
  let i = 0

  while (i < n) {
    const diff = target[i] - nums[i]

    if (diff === 0) {
      i++
      continue
    }

    let start = i
    if (diff > 0) {
      let last = 0
      let min = 0
      // 递增个数
      let k = 0
      let falg = false
      while (i < n && target[i] - nums[i] > 0) {
        const diff = target[i] - nums[i]
        // console.log('[shun]--->1', i, diff)
        if (diff > last) {
          // 递增
          k++
        } else {
          // 分段
          ans += last - min
          min = diff
          k = 1
          falg = true
        }
        last = diff
        // dd.push(diff)
        i++
      }
      if (i > start) {
        // 如果连续递增的只有一个，那么不用额外操作
        if (!falg || k > 1) {
          ans += last - min
        }
      }
    } else {
      let last = 0
      let min = 0
      let res = 0
      // 递增个数
      let k = 0
      let falg = false
      while (i < n && target[i] - nums[i] < 0) {
        const diff = -(target[i] - nums[i])
        if (diff > last) {
          k++
        } else {
          ans += last - min
          min = diff
          k = 1
          falg = true
        }
        last = diff
        i++
      }
      if (i > start) {
        if (!falg || k > 1) {
          ans += last - min
        }
      }
    }
  }

  return ans
}
