class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n=nums.size();
        vector<int> v(n);
        for(int i=0;i<n;i++){
            v[i]=nums[i]-target[i];
        }
        long long c=abs(v[0]),ans=abs(v[0]);
        for(int i=1;i<n;i++){
            if((v[i]<0&&v[i-1]<0)||(v[i]>0&&v[i-1]>0)){
                // cout<<"hi";
                // cout<<c<<" "<<v[i]<<endl;
                ans+=max(abs(v[i])-abs(c),(long long)0);
            }
            else{
                ans+=abs(v[i]);
            }
            c=v[i];
        }
        // cout<<endl;
        return ans;
    }
};
