class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        delta = []
        for a,b in zip(nums, target):
            delta.append(b-a)
        #print(delta)
        res = 0
        t = 0
        for v in delta:
            if v > 0:
                if t <= 0:
                    t = 0
                if v > t:
                    res += v-t
                t = v
            else:
                if t >= 0:
                    t = 0
                if v < t:
                    res += t-v
                t = v
        return res
