class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> b(n), dp(n);
        for(int i=0;i<n;i++){
            b[i]= target[i]-nums[i];
        }
        dp[0]=abs(b[0]);
        for(int i=1;i<n;i++){
            if(b[i]==0) dp[i]=dp[i-1];
            else if(b[i]>0 && b[i-1]>0){
                if(b[i]<=b[i-1]) dp[i]=dp[i-1];
                else{
                    dp[i]= dp[i-1]+(b[i]-b[i-1]);
                }
            }
            else if(b[i]<0 && b[i-1]>=0){
                dp[i]=dp[i-1]+abs(b[i]);
            }
            else if(b[i]>0 && b[i-1]<=0){
                dp[i]=dp[i-1]+b[i];
            }
            else if(b[i]<0 && b[i-1]<0){
                if(b[i]>=b[i-1]) dp[i]=dp[i-1];
                else{
                    dp[i]=dp[i-1]+ abs(b[i]-b[i-1]);
                }
            }
        }
        return dp[n-1];
    }
};
