class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        nums.append(0)
        target.append(0)
        n = len(nums)
        p = nums[0]-target[0]
        u = True
        m = 0
        a = 0
        for i in range(1,n):
            c = nums[i]-target[i]
            if p > 0 and c > 0:
                if u:
                    if p > c:
                        a += p-m
                        u = False
                else:
                    if c > p:
                        m = p
                        u = True
            elif p < 0 and c < 0:
                if u:
                    if c > p:
                        a += m - p
                        u = False
                else:
                    if p > c:
                        m = p
                        u = True
            else:
                if u:
                    if p > 0:
                        a += p-m
                    else:
                        a += m-p
                m = 0
                u = True
            p = c
        return a
