class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long total = 0;
        long long c = 0;
        vector<int> v(n);
        for (int i = 0; i < n; ++i) {
            v[i] = target[i] - nums[i];
        }

        for (int i = 0; i < n; ++i) {
            if (i == 0 || (v[i - 1] < 0 && v[i] < 0) || (v[i - 1] > 0 && v[i] > 0) ) {
                int a_op = abs(v[i]) - abs(c);
                if (a_op > 0) {
                    total += a_op;
                }
                c = v[i];
            } 
            else {
                total += abs(v[i]);
                c = v[i];
            }
        }
        return total;
    }
};
