class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n=nums.size();
        vector<int>diff(n);
        for(int i=0;i<n;i++){
            diff[i]=t[i]-nums[i];
        }
        long long ans=0;
        int cdif=0;
        for(int i=0;i<n;i++){
            if(i==0){
                int addi=abs(diff[i])-abs(cdif);
                if(addi>0){
                    ans+=addi;
                }
                cdif=diff[i];
            }
            else if((diff[i-1]>0 && diff[i]>0) ){
                int addi=abs(diff[i])-abs(cdif);
                if(addi>0){
                    ans+=addi;
                }
                cdif=diff[i];
            }
            else if((diff[i-1]<0 && diff[i]<0)){
                int addi=abs(diff[i])-abs(cdif);
                if(addi>0){
                    ans+=addi;
                }
                cdif=diff[i];
            }
            else{
                ans+=abs(diff[i]);
                cdif=diff[i];
            }

        }
        return ans;
    }
};
