from typing import List

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        p,d,s,a = 0,0,0,0  
        n = len(nums)

        for i in range(n):
            if p == 0:
                if nums[i] < target[i]:
                    p = 1
                    d = target[i] - nums[i]
                    s += 1
                elif nums[i] > target[i]:
                    p = -1
                    d = nums[i] - target[i]
                    s += 1
                else:
                    continue
            else:
                if (nums[i] < target[i] and p == 1 and d == target[i] - nums[i]) or \
                   (nums[i] > target[i] and p == -1 and d == nums[i] - target[i]):
                    s += 1
                    continue
                elif nums[i] < target[i] and p == 1:
                    if target[i] - nums[i] > d:
                        s = 1
                        d = target[i] - nums[i]
                    else:
                        a += d - target[i] + nums[i]
                        d = target[i] - nums[i]
                    continue
                elif nums[i] > target[i] and p == -1:
                    if nums[i] - target[i] > d:
                        s = 1
                        d = nums[i] - target[i]
                    else:
                        a += d + target[i] - nums[i]
                        d = nums[i] - target[i]
                    continue
                elif nums[i] == target[i]:
                    a += d
                    p,s,d = 0,0,0

                else:
                    a += d
                    if nums[i] < target[i]:
                        p = 1
                        d = target[i] - nums[i]
                        s += 1
                    else:
                        p = -1
                        d = nums[i] - target[i]
                        s += 1

        return a + d
