class Solution {
public:
    long long minimumOperations(vector<int>& snum, vector<int>& k) {
        int n = snum.size();
        vector<long long> sdp(n);
        long long maxi=*max_element(snum.begin(),snum.end());
        for (int i = 0; i < n; i++)  sdp[i] = (long long)(k[i]) - snum[i];
        long long sssans = 0;
        long long scd = 0;
        for (int i=0;i<n;i++) {
            if (!i || (sdp[i-1]>0 && sdp[i]>0) || (sdp[i-1]<0 && sdp[i]<0)) {
                long long stmp = abs(sdp[i]) - abs(scd);
                if (stmp > 0) sssans += stmp;
                scd = sdp[i];
            } else {
                sssans += abs(sdp[i]);
                scd = sdp[i];
            }
        }
        return sssans;
    }
};
